mapper.update:
		update td_m_card_type


      		CARD_TYPE_NAME = #{cardTypeName,jdbcType=VARCHAR},


    	where CARD_TYPE_CODE = #{cardTypeCode,jdbcType=VARCHAR}
    	and MERCHANT_ID = #{merchantId,jdbcType=VARCHAR}

mapper.select:
SELECT ct.CARD_TYPE_CODE, ct.CARD_TYPE_NAME, ct.MERCHANT_ID, ct.STATE
FROM td_m_card_type ct
WHERE ct.MERCHANT_ID = #{merchantId,jdbcType=VARCHAR}
